mapper.select:
  	select gmember.id, member.name, member.nickname, gmember.gender, gmember.birth, member.tel
	from member member, genericmember gmember
	where member.id=gmember.id and gmember.id=#{id}

mapper.insert:

	select nickname from member where id=#{companyId}

INSERT into mileage (mileage_no,deal_money,deal_content,id) 
VALUES (mileage_seq.nextval,#{dealMoney},#{nickname},#{memberId})
